(4, 'fe5ab7ae-6099-4b12-9c85-6099f2f971a9', 'MDIP 2023', 'GAFCSC is open to all other Security Service Personnel, as well as individuals interested in Defence and International Politics.', NULL, 0, 5, '2023-09-08 14:49:23', '2024-11-05 22:07:12'),
(7, '90e26cb0-7c1d-4bd8-b870-e37804f6ceed', 'GRADUANDS RECEIVE SCROLLS', 'A cross-section of students of Senior Staff Course 44 & MDIP 2023 lined up to receive graduation scrolls after successfully meeting all requirements for the award of the “PSC” and “MDIP” qualifications respectively.', NULL, 0, 3, '2023-09-09 00:16:48', '2024-11-05 22:25:51'),
(8, '329de29f-d739-41ee-9cb8-65443fff70d7', 'DIRECTING STAFF AND STUDENTS FROM THE NAVAL FACULTY', 'A cross-section of Directing Staff (DS) of the Naval Faculty of the Ghana Armed Forces Command and Staff College and some Naval Students Officers of Senior Staff Course 43.', NULL, 0, 6, '2023-09-09 00:17:21', '2024-11-05 22:06:56'),
(12, 'b9c3d38d-89b5-4543-a71c-1893d737c8c8', 'THE CENTRE OF EXCELLENCE              #ALITE FLAMMAM', 'Welcome to the official website of the Ghana Armed Forces Command and Staff College (GAFCSC), the centre of academic excellence of the Ghana Armed Forces.', NULL, 1, 1, '2023-09-11 16:48:39', '2023-10-09 19:41:11'),
(13, '5002e838-ba29-4dd5-ad0b-610049d39ca2', 'A SHOT OF THE GAFCSC ACADEMIC FACULTY', 'The Academic Board, Staff and Policy Committee in a group Photo with Students of Senior Staff Course 44.', NULL, 0, 4, '2023-09-11 16:51:26', '2024-11-05 22:07:27'),
(14, 'fdb668bc-dd0d-4fc1-8584-8864041d65d2', 'LASW DOCTRINE INCLINED', 'The GAFCSC is inclined towards training future commanders and interest persons on the concept of Land, Air & Sea Warfare (LASW) and employs various models to equip students with the requisite knowledge within the LASW domain.', NULL, 0, 2, '2023-09-11 16:52:39', '2024-11-12 12:49:33'),
(16, 'a1bda87b-f790-4ac2-96c8-fc8785d446bf', 'AIR COMBAT POWER WEEK', 'The Ghana Armed Forces Command and Staff College is embarking on a week long Air Combat Power Week from 28th October to 1st November, featuring activities and programs to enhance skills and strategic understanding in the air force domain.', NULL, 0, 5, '2024-10-22 10:44:58', '2024-11-12 12:48:29'),
(18, 'cae91e18-7be1-4492-a655-a788802c4d2b', 'LAND COMBAT POWER', 'The Ghana Armed Forces Command and Staff College embarked on a week long Land Combat Power Week in October 2024 , featuring activities and programs to enhance skills and strategic understanding on land.', NULL, 0, 4, '2024-11-05 22:19:12', '2024-11-12 12:48:52'),
(20, 'b67a1443-0e0d-4152-88ce-da5aca01ee3e', 'MARITIME SECTOR GOVERNANCE COURSE', 'From 21st to 25th November 2024, GAFCSC will host a course for maritime practitioners and stakeholders on effective maritime sector governance.', NULL, 1, 2, '2024-11-13 13:35:41', '2024-11-13 13:40:25');

