CREATE TABLE `careers` (
  `id` bigint UNSIGNED NOT NULL,
  `uuid` char(36) COLLATE utf8mb4_unicode_ci NOT NULL,
  `position` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` longtext COLLATE utf8mb4_unicode_ci,
  `deadline` date DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `careers`
--

INSERT INTO `careers` (`id`, `uuid`, `position`, `description`, `deadline`, `created_at`, `updated_at`) VALUES
(1, 'ca03c18b-eb82-4b57-bcb9-2e19124e8f2f', 'ASSISTANT RESEARCH FELLOW/ RESEARCH ASSISTANT', '<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Introduction</span></p>\n</div>\n</div>\n<div class=\"section\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">The expansion of the Ghana Armed Forces Command and Staff College (GAFCSC)&nbsp;</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">and in&nbsp;</span>recognition of the College hosting the establishment of the National College of Defense Studies (War College), there is a need to employ Assistant Research Fellows also known as Research Assistants.</p>\n</div>\n</div>\n</div>\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Research Assistants are Academic positions hired to assist Faculty in carrying out a particular&nbsp;</span>research agenda. The title/pay level is determined based on current academic status.</p>\n</div>\n</div>\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Need and Purpose of a Research Assistant</span></span></p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">To support research projects, conducting research, analysing results, and generating original ideas.&nbsp;Conduct tutorials (teaching assistant) and assist in the supervision of student research projects, development of student research skills and assessment of student knowledge in accordance with the Ghana Armed Forces Command and Staff College&rsquo;s (GAFCSC) strategy, policy and procedures.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Job Description</span></p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">As a Research Assistant, the main goal of the position is to make sure that all of the information in research databases are up-to-date and accurate.&nbsp;The successful applicant for this position will&nbsp;manage the collection, maintenance, and analysis of research data for administrative database&nbsp;studies. Working under the supervision of the principal Faculty/Investigator, the Research&nbsp;</span>Assistant will conduct and interpret statistical analyses, prepare presentations and manuscripts, conduct tutorials (teaching assistant), and coordinate study activities.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Minimum qualifications/requirements</span></span></p>\n</div>\n</div>\n<div class=\"section\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<ul>\n<li> <i class=\"far fa-check\"></i> A Research Masters Degree from a recognized university in a related field of study to the programmes on offer, with at least a year&rsquo;s post-qualification experience.</li>\n<li> <i class=\"far fa-check\"></i> Excellent administration skills.</li>\n<li> <i class=\"far fa-check\"></i> Possess sufficient knowledge in the discipline and of research methodology and methods, and techniques to work within established research programmes.</li>\n<li> <i class=\"far fa-check\"></i> Evidence of research activity and published research is desirable.</li>\n<li> <i class=\"far fa-check\"></i> Excellent written and verbal communication skills.</li>\n<li> <i class=\"far fa-check\"></i> Ability to communicate material of a specialist or highly technical nature.</li>\n<li> <i class=\"far fa-check\"></i> Ability to multi-task (excellent time management skills) &ndash; able to manage research and administrative activities and to balance competing pressures of research, teaching and administrative demands and deadlines.</li>\n<li> <i class=\"far fa-check\"></i> Good inter-personal skills with the ability to liaise with colleges and students.</li>\n<li> <i class=\"far fa-check\"></i> Able to build contacts and participate in internal and external networks for the exchange of information and collaboration.</li>\n<li> <i class=\"far fa-check\"></i> Able to identify potential sources of funding.</li>\n<li> <i class=\"far fa-check\"></i> Academic and/or work experience related to the field of research, as defined by the faculty and/or lead researcher.</li>\n<li> <i class=\"far fa-check\"></i> Experience with specialized software or datasets, as required by the faculty.</li>\n<li> <i class=\"far fa-check\"></i> Satisfactory academic progress, as defined by the faculty.</li>\n</ul>\n</div>\n</div>\n<div class=\"section\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<div class=\"layoutArea\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<div class=\"column\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Responsibilities</span></p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">&nbsp;While a research assistant is expected to contribute in many ways to the research project, typical responsibilities include the following:</p>\n<ul style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 23px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; line-height: 26px;\">\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Collect and analyze data.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Prepare materials for submission to granting agencies and foundations.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Prepare research interview questions.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Recruit and/or interview participants/ subjects.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Maintain accurate records of interviews, safeguarding the confidentiality of participants/subjects, as necessary.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Summarize interviews.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Provide ready access to all experimental data for the faculty researcher and/or supervisor.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Request or acquire equipment or supplies necessary for a project.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Manage and respond to research/project related corresponds/emails.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Prepare, maintain, and update website materials.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Attend research/project meetings.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Attend discipline/subject area seminars and other meetings as necessary.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Summarize research/project results.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Prepare other articles, reports, and presentations including tutorials.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Monitor the research/project budget.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Undertake research and publish at least one article per year.</li>\n</ul>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Other Duties</span></p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">&nbsp;The job description indicates the expectations of staff at this level. With reasonable notice and support, the GAFCSC can expect and require a member of staff at this level to undertake any of the activities detailed above. You will be expected to perform at this level within a reasonable period following promotion. Job descriptions are not exhaustive, and you may be required to undertake other duties of similar level and responsibility.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Other duties and responsibilities may be assigned by individual faculty/researchers. Quarterly work requirements and schedules must be discussed with the Faculty/Supervisor to ensure that the work priorities can be accomplished for the&nbsp; quarter/duration. Concerns about any additional responsibilities should be discussed first with the Faculty/Supervisor (Faculty researcher), and then the Dean of Academic Division or the Department Head.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">This position is subject to policies stated and reviewed by the Ghana Armed Forces Command and Staff College (GAFCSC) via the Academic Division.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Mode of Application</span></p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">All applications with relevant documents including a cover letter specifying clearly the position being applied for should be sent by post/electronically to:</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">The Registrar</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Ghana Armed Forces Command and Staff College (GAFCSC)</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Otu Barracks</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Teshie, Accra, Ghana.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Email: recruitment@gafcscmil.edu.gh</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">The deadline for submission of application is&nbsp;<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Monday 22nd May, 2023 at 1700hrs</span>. Shortlisted applicants will be contacted and invited for an interview.</p>\n</div>\n</div>\n</div>\n</div>\n</div>', '2023-07-12', '2023-09-15 16:24:42', '2023-09-15 16:48:37'),
(2, 'bd14a41e-27c8-4bb2-ab29-d26b5b947765', 'ASSISTANT REGISTRAR', '<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">The Academic Division of the Ghana Armed Forces Command and Staff College is calling for applications for employment to the position of Assistant Registrar.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\"><u style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Main Function</u>&nbsp;&ndash; The Assistant Registrar shall oversee the management of innovative and progressive academic programs and systems that support students in their academic pursuits, while enhancing student life and the GAFCSC academic community.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">This application is open to all persons possessing the following specifications:</p>\n<ol style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 23px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; list-style-position: inside; list-style-image: initial; line-height: 26px; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Be a resident Ghanaian;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Not more than 40 years of age;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">A Masters&rsquo; Degree from a recognized institution, preferably in the field of Business Administration;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">At least 4 years progressive post-qualification experience in a senior administrative role in higher education;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Strong leadership qualities and a thorough understanding of the tertiary system in Ghana;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Ability to work with all staff to develop positive customer-oriented services to students and faculty;</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Strong communication and interpersonal skills.</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Sound knowledge and use of MS Office Suite including Word, Excel and PowerPoint.</li>\n</ol>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\"><u style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Required Documents</u>&nbsp;&ndash; Applicants are to submit the following documents to the Office of the Registrar:</p>\n<ul style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 23px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; list-style-position: initial; list-style-image: initial; line-height: 26px; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Application Letter</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">1 Certified Copy of Master&rsquo;s Degree Certificate</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">1 Certified Copy of Bachelor&rsquo;s Degree Certificate</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">1 Original Transcript from the awarding institution</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Curriculum Vitae (CV)</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">Letter of Motivation (Reason for applying for this position)</li>\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent;\">1 Copy of Birth Certificate</li>\n</ul>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">NB: Women are strongly encouraged to apply for this position.</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">All documents should be submitted directly to:</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">The Registrar</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">Ghana Armed Forces Command and</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">Staff College (GAFCSC)</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\">Otu Barracks, Teshie, Accra</p>\n<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: #ffffff; color: #666666; font-family: Montserrat, Helvetica, Arial, Lucida, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; font-weight: bold;\">Deadline for Submission of Applications is Friday 11<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-size-adjust: 100%; vertical-align: baseline; background: transparent; height: 0px; line-height: 1; position: relative; bottom: 0.8em;\">th</span>&nbsp;February, 2022.</span></p>', '2022-02-11', '2022-09-30 16:25:26', '2023-09-15 16:25:26');

